package pl.szymontomalik.PhotoGalleryApp.controllers;import lombok.RequiredArgsConstructor;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.GetMapping;import pl.szymontomalik.PhotoGalleryApp.services.UserService;@Controller@RequiredArgsConstructorpublic class MainPageController {    private final UserService userService;    @GetMapping("/index")    public String mainPage() {        if (userService.isLogged()) {            return "index";        }        return "login";    }}