package pl.szymontomalik.PhotoGalleryApp.entities;import lombok.Getter;import lombok.Setter;import org.springframework.security.crypto.bcrypt.BCrypt;import javax.persistence.*;import javax.validation.constraints.NotBlank;import javax.validation.constraints.Size;import java.util.List;@Entity@Getter@Setter@Table(name = "albums")public class Album {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private long id;    @NotBlank (message = "Album name is mandatory" )    private String name;    @NotBlank(message = "Password is mandatory")    @Size(min = 8)    private String password = BCrypt.hashpw("", BCrypt.gensalt());    @OneToMany    private List<Photography> photos;}